// App.js
import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { auth } from './firebaseConfig';
import HomePage from './components/HomePage';
import LoginPage from './components/LoginPage';

function App() {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const unsubscribe = auth.onAuthStateChanged((currentUser) => {
      setUser(currentUser);
      setLoading(false); // Set loading to false after the check
    });
    return () => unsubscribe();
  }, []);

  if (loading) {
    return <p>Loading...</p>; // Show a loading message while checking auth
  }

  return (
    <Router>
      <Routes>
        <Route
          path="/"
          element={user ? <HomePage /> : <Navigate to="/login" />} // Redirect to login if not authenticated
        />
        <Route
          path="/login"
          element={user ? <Navigate to="/" /> : <LoginPage />} // Redirect to homepage if already logged in
        />
      </Routes>
    </Router>
  );
}

export default App;
