// AddPostPage.js
import React, { useState } from 'react';
import { db } from '../firebaseConfig';
import { collection, addDoc } from 'firebase/firestore';
import { useNavigate } from 'react-router-dom';

function AddPostPage() {
  const [fileName, setFileName] = useState('');
  const navigate = useNavigate();

  // Handle file selection and extract the file name
  const handleFileChange = (e) => {
    const file = e.target.files[0]; // Get the first selected file
    if (file) {
      setFileName(file.name); // Set the file name only, not the content
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Store only the file name in Firebase
      await addDoc(collection(db, 'posts'), {
        title: new Date().toLocaleString(),
        fileName,
      });
      console.log('File name added to Firebase');
      setFileName(''); // Clear the file name
      navigate('/'); // Navigate back to the homepage after submission
    } catch (error) {
      console.error('Error adding file name to Firebase:', error);
    }
  };

  return (
    <div className="add-post-page">
      <h2>Add New Post</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="file"
          onChange={handleFileChange} // Capture file selection
        />
        <button type="submit" disabled={!fileName}>Submit</button>
      </form>
    </div>
  );
}

export default AddPostPage;
