// AddPostPage.js
import React, { useState } from 'react';
import { db } from '../firebaseConfig';
import { collection, addDoc } from 'firebase/firestore';
import { useNavigate } from 'react-router-dom';
import './AddPostPage.css'; // Add CSS styling for the slider

function AddPostPage() {
  const [fileName, setFileName] = useState('');
  const [delay, setDelay] = useState('');
  const [isDelayInHours, setIsDelayInHours] = useState(false);
  const [isPublic, setIsPublic] = useState(true); // New state for visibility
  const navigate = useNavigate();

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setFileName(file.name);
    }
  };

  const handleDelayChange = (e) => {
    const value = parseInt(e.target.value, 10);
    setIsDelayInHours(value > 300);
    setDelay(value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const delayValue = isDelayInHours ? (delay / 60).toFixed(2) : delay;
      const delayUnit = isDelayInHours ? 'hours' : 'minutes';
      const currentTime = new Date();
      const displayTime = new Date(currentTime.getTime() + delay * 60000);

      // Save file name, delay, display time, and visibility setting to Firebase
      await addDoc(collection(db, 'posts'), {
        title: currentTime.toLocaleString(),
        fileName,
        delay: `${delayValue} ${delayUnit}`,
        displayTime: displayTime.toISOString(),
        isPublic, // Store visibility setting
      });

      console.log('File name, visibility, and delay added to Firebase');
      setFileName('');
      setDelay('');
      setIsDelayInHours(false);
      setIsPublic(true); // Reset to Public by default
      navigate('/');
    } catch (error) {
      console.error('Error adding data to Firebase:', error);
    }
  };

  return (
    <div className="add-post-page">
      <h2>Add New Post</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="file"
          onChange={handleFileChange}
        />
        <input
          type="number"
          placeholder="Time before it's visible (in minutes)"
          value={delay}
          onChange={handleDelayChange}
          min="0"
          required
        />
        {isDelayInHours && <p>(Stored in hours)</p>}

        {/* Visibility Toggle */}
        <label className="switch">
          <span>{isPublic ? "Public" : "Private"}</span>
          <input
            type="checkbox"
            checked={isPublic}
            onChange={() => setIsPublic(!isPublic)}
          />
          <span className="slider"></span>
        </label>

        <button type="submit" disabled={!fileName || !delay}>Submit</button>
      </form>
    </div>
  );
}

export default AddPostPage;
