// AddPostPage.js
import React, { useState } from 'react';
import { db } from '../firebaseConfig';
import { collection, addDoc } from 'firebase/firestore';
import { useNavigate } from 'react-router-dom';

function AddPostPage() {
  const [fileName, setFileName] = useState('');
  const [delay, setDelay] = useState(''); // State for delay time in minutes
  const navigate = useNavigate();

  // Handle file selection and extract the file name
  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setFileName(file.name); // Only store the file name
    }
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Calculate the display time based on the delay
      const currentTime = new Date();
      const displayTime = new Date(currentTime.getTime() + delay * 60000); // Add delay in ms

      // Save file name, delay, and display time to Firebase
      await addDoc(collection(db, 'posts'), {
        title: currentTime.toLocaleString(), // Store current date/time as the title
        fileName,
        delay: parseInt(delay, 10), // Store delay in minutes as a number
        displayTime: displayTime.toISOString(), // Store the calculated display time
      });

      console.log('File name and delay added to Firebase');
      setFileName('');
      setDelay('');
      navigate('/');
    } catch (error) {
      console.error('Error adding data to Firebase:', error);
    }
  };

  return (
    <div className="add-post-page">
      <h2>Add New Post</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="file"
          onChange={handleFileChange} // Capture the file selection
        />
        <input
          type="number"
          placeholder="Delay in minutes"
          value={delay}
          onChange={(e) => setDelay(e.target.value)}
          min="0"
          required
        />
        <button type="submit" disabled={!fileName || !delay}>Submit</button>
      </form>
    </div>
  );
}

export default AddPostPage;
