// HomePage.js
import React, { useEffect, useState } from 'react';
import { db } from '../firebaseConfig';
import { collection, getDocs } from 'firebase/firestore';
import Logout from './Logout';
import '../App.css';

function HomePage() {
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    const fetchPosts = async () => {
      const postsCollection = collection(db, 'posts');
      const postSnapshot = await getDocs(postsCollection);
      const postList = postSnapshot.docs.map(doc => doc.data());
      setPosts(postList);
    };

    fetchPosts();
  }, []);

  // Function to calculate remaining time
  const calculateRemainingTime = (displayTime) => {
    const currentTime = new Date();
    const displayDate = new Date(displayTime);
    const remainingTime = displayDate - currentTime;

    // If remaining time is greater than zero, calculate in minutes
    if (remainingTime > 0) {
      const remainingMinutes = Math.ceil(remainingTime / 60000); // Convert ms to minutes
      return `${remainingMinutes} minutes`;
    }
    return 'now';
  };

  return (
    <div className="home-page">
      <header className="app-header">
        <h1 className="app-title">FutureGram</h1>
        <Logout />
      </header>

      <div className="post-list-section">
        <h3>FutureGram Posts</h3>
        <div className="post-list">
          {posts.map((post, index) => {
            const remainingTimeText = calculateRemainingTime(post.displayTime);

            return (
              <div key={index} className="post-item">
                <p>{post.title}</p>
                {remainingTimeText !== 'now' ? (
                  <p>Post will be available in {remainingTimeText}</p>
                ) : (
                  <img
                    src={require(`../images/${post.fileName}`).default}
                    alt={post.fileName}
                    className="post-image"
                  />
                )}
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
}

export default HomePage;
